import React, { useState } from 'react';
import { ArrowRight, ArrowLeft, Car, DollarSign, Users, CheckCircle, Brain } from 'lucide-react';

const CarLeaseQuiz = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [isLoading, setIsLoading] = useState(false);
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    email: '',
    cellphone:'',
    zipcode: '',
    car_interest_level:'',
    q1_primary_usage: [],
    q1_primary_usage_other: '',
    q1_known_exact_make: [],
    q1_known_exact_make_other: '',
    q2_car_type: [],
    q2_known_exact_car_type: [],
    q3_car_make: [],
    q3_car_make_other: '',
    q4_car_features: [],
    q4_car_features_other: '',
    q5_car_drivetrain: [],
    q6_leaseterm:[],
    q6_known_exact_leaseterm: [],
    q7_leasemiles: '',
    q7_known_exact_leasemiles: '',
    yearly_income: '',
    lease_budget:'',
    dp_budget: ''
  });

  const totalSteps = 4;

  const formatCellPhoneNumber = (value) => {

    const cellphone = value.replace(/\D/g, '');
    const phoneLength = cellphone.length;
    if (phoneLength < 4) return cellphone;
    if (phoneLength < 7) return `(${cellphone.slice(0, 3)}) ${cellphone.slice(3)}`;
    return `(${cellphone.slice(0, 3)}) ${cellphone.slice(3, 6)}-${cellphone.slice(6, 10)}`;
  };

  const isValidPhoneNumber = (number) => {
  const digitsOnly = number.replace(/\D/g, '');
  return digitsOnly.length === 10;
  };

  const handlePhoneChange = (value) => {
    const formatted = formatCellPhoneNumber(value);
    setFormData({ ...formData, cellphone: formatted });
  };

  const isValidEmail = (email) => {
  return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  const isValidZipcode = (zipcode) => {
  return /^\d{5}$/.test(zipcode);
};

  const handleInputChange = (field, value) => {
    setFormData(prev => ({...prev,[field]: value}));
  };

  const formatCurrency = (value) => {
    if (!value) return '';
    const number = value.toString().replace(/[^\d]/g, '');
    return new Intl.NumberFormat('en-US', { style: 'currency', currency: 'USD' }).format(number);
    };

  const handleCheckboxChange = (field, value, isChecked) => {
  setFormData((prev) => {
    const currentArray = prev[field] || []; // fallback to empty array
    const updated = isChecked
      ? [...currentArray, value] // Add the selected value
      : currentArray.filter((item) => item !== value); // Remove it

    return { ...prev, [field]: updated };
  });
};

  const handleFeatureToggle = (feature) => {
    setFormData(prev => ({
      ...prev,
      importantFeatures: prev.importantFeatures.includes(feature)
        ? prev.importantFeatures.filter(f => f !== feature)
        : [...prev.importantFeatures, feature]
    }));
  };

  const nextStep = () => {
    if (currentStep < totalSteps) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = async () => {
    if (!isStepValid(currentStep)) {
      alert('Please fill in all required fields before submitting.');
      return;
    }

    try {
      console.log('Submitting quiz and generating recommendations...');

      // Show loading state
      setIsLoading(true);

      const response = await fetch('http://localhost:3001/api/generate-recommendations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          quiz_data: formData
        })
      });

      const result = await response.json();

      if (result.success) {
        // Store recommendations and navigate to results
        console.log('Recommendations generated:', result.recommendations);
        alert('Recommendations generated! Redirecting to results...');
        // Here you'll later navigate to the results page

      } else {
        console.warn('Using fallback recommendations:', result.fallback_recommendations);
        alert('Recommendations ready! (Using backup system)');
      }

    } catch (error) {
      console.error('Error generating recommendations:', error);
      alert('There was an error generating recommendations. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const renderProgressBar = () => (
    <div className="w-full bg-gray-200 rounded-full h-2 mb-8">
      <div 
        className="bg-blue-600 h-2 rounded-full transition-all duration-300 ease-out"
        style={{ width: `${(currentStep / totalSteps) * 100}%` }}
      ></div>
    </div>
  );

  const requiredFieldsByStep = {
  1: ['first_name', 'last_name', 'email', 'cellphone', 'zipcode'],
  2: ['car_interest_level'],
  3: (formData) => (
    formData.car_interest_level === 'need_help' || formData.car_interest_level === 'have_idea'
      ? ['q1_primary_usage', 'q2_car_type', 'q3_car_make', 'q4_car_features', 'q5_car_drivetrain', 'q6_leaseterm', 'q7_leasemiles']
      : ['q1_known_exact_make', 'q2_known_exact_car_type', 'q6_known_exact_leaseterm', 'q7_known_exact_leasemiles']
  ),
  4: ['yearly_income', 'lease_budget_min', 'lease_budget_max', 'dp_budget']
};

  const isStepValid = (step) => {
  const requiredFields = typeof requiredFieldsByStep[step] === 'function'
    ? requiredFieldsByStep[step](formData)
    : requiredFieldsByStep[step];

  return requiredFields.every((field) => {
    const value = formData[field];

    if (Array.isArray(value)) return value.length > 0;

    // Specific validation for certain fields
    if (field === 'email') return isValidEmail(value);
    if (field === 'cellphone') return isValidPhoneNumber(value);
    if (field === 'zipcode') return isValidZipcode(value);

    return value && value.toString().trim() !== '';
  });
};

