import React from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ReferenceLine } from 'recharts';

const LeaseApprovalChart = ({ monthlyIncome, creditScore }) => {

    const creditRanges = [500,550,600,650,700,750,800];

  const getLeaseAmount = (score) => {
    if (score >= 700) return monthlyIncome * 0.15;
    if (score >= 650) return monthlyIncome * 0.14;
    if (score >= 600) return monthlyIncome * 0.13;
    return monthlyIncome * 0.12;
  };

  const data = creditRanges.map(score => ({
    creditScore: score,
    leaseAmount: Math.round(getLeaseAmount(score)),
  }));

  return (
    <div className="w-full flex justify-center mt-8">
      <LineChart width={500} height={300} data={data}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="creditScore" />
        <YAxis />
        <Tooltip />
        <Line type="monotone" dataKey="leaseAmount" stroke="#3b82f6" strokeWidth={3} />
        <ReferenceLine
          x={creditScore}
          stroke="red"
          strokeDasharray="4 4"
          label={{ value: `Your Score (${creditScore})`, fill: 'red', position: 'top' }}
        />
      </LineChart>
    </div>
  );
};

export default LeaseApprovalChart;
